Click to expand full code <pre><code>#include &lt;U8g2lib.h&gt;
#include &lt;Wire.h&gt;
// Screen（SH1106 OLED）
#define OLED_SDA 19
#define OLED_SCL 23
// EC11 encoder
#define ROTARY_A     5
#define ROTARY_B     17
#define ROTARY_PUSH  18
// button
#define BTN_CONFIRM  22
#define BTN_BACK     16
// buzzer
#define BUZZER_PIN   21  
// manu
const int menuCount = 5;
const char* menuItems[menuCount] = {
  "Pomodoro 10 min",
  "Pomodoro 15 min",
  "Pomodoro 20 min",
  "Pomodoro 25 min",
  "Info"
};
// variable status
int menuIndex = 1;
int lastCLK = HIGH;
bool inCountdown = false;
U8G2_SH1106_128X64_NONAME_F_HW_I2C u8g2(U8G2_R0, /* reset=*/ U8X8_PIN_NONE);
// ====== SETUP ======
void setup() {
  pinMode(ROTARY_A, INPUT_PULLUP);
  pinMode(ROTARY_B, INPUT_PULLUP);
  pinMode(ROTARY_PUSH, INPUT_PULLUP);
  pinMode(BTN_CONFIRM, INPUT_PULLUP);
  pinMode(BTN_BACK, INPUT_PULLUP);
  pinMode(BUZZER_PIN, OUTPUT);
  //digitalWrite(BUZZER_PIN, HIGH); // 不响（低电平才响）
  Wire.begin(OLED_SDA, OLED_SCL);
  u8g2.begin();
  drawMenu();
}
// ====== LOOP ======
void loop() {
  if (inCountdown) {
    // 倒计时中仅监听退出
    if (digitalRead(BTN_BACK) == LOW) {
      delay(200);
      inCountdown = false;
      drawMenu();
    }
    return;
  }
  handleRotary();
  // 开始倒计时（旋钮按下或Confirm）
  if (digitalRead(ROTARY_PUSH) == LOW || digitalRead(BTN_CONFIRM) == LOW) {
    delay(200);
    handleSelection(menuIndex);
    drawMenu();
  }
  // Back 返回主菜单
  if (digitalRead(BTN_BACK) == LOW) {
    delay(200);
    drawMenu();
  }
}
// ====== HANDLE ROTARY ======
void handleRotary() {
  int currentCLK = digitalRead(ROTARY_A);
  if (currentCLK != lastCLK &amp;&amp; currentCLK == LOW) {
    if (digitalRead(ROTARY_B) != currentCLK) {
      menuIndex++;
    } else {
      menuIndex--;
    }
    if (menuIndex &lt; 1) menuIndex = 1;
    if (menuIndex &gt; menuCount) menuIndex = menuCount;
    drawMenu();
  }
  lastCLK = currentCLK;
}
// ====== DRAW MENU ======
void drawMenu() {
  u8g2.clearBuffer();
  u8g2.setFont(u8g2_font_6x10_tr);
  const int visibleCount = 4;
  int startIdx = menuIndex - 1;
  if (startIdx &lt; 1) startIdx = 1;
  if (startIdx &gt; menuCount - visibleCount + 1)
    startIdx = menuCount - visibleCount + 1;
  for (int i = 0; i &lt; visibleCount; i++) {
    int idx = startIdx + i;
    if (idx &gt; menuCount) break;
    int y = i * 16 + 14;
    if (idx == menuIndex) {
      u8g2.drawBox(0, i * 16, 128, 16);
      u8g2.setDrawColor(0);
    } else {
      u8g2.setDrawColor(1);
    }
    u8g2.setCursor(10, y);
    u8g2.print(menuItems[idx - 1]);
  }
  u8g2.setDrawColor(1);
  u8g2.sendBuffer();
}
// ====== HANDLE SELECTION ======
void handleSelection(int index) {
  if (index &gt;= 1 &amp;&amp; index &lt;= 4) {
    int minutes[] = {10, 15, 20, 25};
    int seconds = minutes[index - 1] * 60;
    inCountdown = true;
    for (int i = seconds; i &gt;= 0 &amp;&amp; inCountdown; i--) {
      u8g2.clearBuffer();
      u8g2.setFont(u8g2_font_logisoso32_tr);
      int min = i / 60;
      int sec = i % 60;
      char timeStr[6];
      sprintf(timeStr, "%02d:%02d", min, sec);
      int textWidth = u8g2.getStrWidth(timeStr);
      u8g2.setCursor((128 - textWidth) / 2, 45);
      u8g2.print(timeStr);
      u8g2.sendBuffer();
      delay(1000);
    }
    if (inCountdown) {
      flashDone();
      beep3Times();
    }
    inCountdown = false;
  } else if (index == 5) {
    u8g2.clearBuffer();
    u8g2.setFont(u8g2_font_6x10_tr);
    u8g2.setCursor(10, 25);
    u8g2.print("Made by Miao");
    u8g2.setCursor(10, 40);
    u8g2.print("ESP32 Pomodoro Timer");
    u8g2.sendBuffer();
    delay(3000);
  }
}
// ====== BEEP FUNCTION ======
void beep3Times() {
  for (int i = 0; i &lt; 3; i++) {
    digitalWrite(BUZZER_PIN, LOW);
    delay(200);
    digitalWrite(BUZZER_PIN, HIGH);
    delay(200);
  }
}
// ====== FLASH DONE ======
void flashDone() {
  for (int i = 0; i &lt; 4; i++) {
    u8g2.clearBuffer();
    u8g2.setFont(u8g2_font_ncenB14_tr);
    const char* text = "Done!";
    int w = u8g2.getStrWidth(text);
    u8g2.setCursor((128 - w) / 2, 40);
    u8g2.setDrawColor(1);
    u8g2.print(text);
    u8g2.sendBuffer();
    delay(300);
    u8g2.clearBuffer();
    u8g2.drawBox(0, 0, 128, 64);
    u8g2.setDrawColor(0);
    u8g2.setCursor((128 - w) / 2, 40);
    u8g2.print(text);
    u8g2.sendBuffer();
    delay(300);
  }
  u8g2.setDrawColor(1);
}
