      u8g2.setDrawColor(1);
    }
    u8g2.setCursor(10, y);
    u8g2.print(menuItems[idx - 1]);
  }
  u8g2.setDrawColor(1);
  u8g2.sendBuffer();
}

void handleSelection(int index) {
  if (index &gt;= 1 &amp;&amp; index &lt;= 4) {
    int seconds = 5 * index + 5;  // index=1 → 10, 2 → 15 ... 4 → 25
    for (int i = seconds; i &gt;= 0; i--) {
